    setBlogs(newBLogs);


  }


  return (
    <div className="home">
   <BlogList blogs={blogs} title='All Blogs are Here!' handleDelete={handleDelete}/>
   <BlogList blogs={blogs.filter((blog)=>blog.author==='mario' )} title="All Mario's blogs are here!"  handleDelete={handleDelete}/>
