import { useState,useEffect } from "react";
import BlogList from "./Blog-list";


const Home = () => {
  const [blogs, setBlogs] = useState([
    { title: 'My new website', body: 'lorem ipsum...', author: 'mario', id: 1 },
    { title: 'Welcome party!', body: 'lorem ipsum...', author: 'yoshi', id: 2 },
    { title: 'Web dev top tips', body: 'lorem ipsum...', author: 'mario', id: 3 }
  ]);

  const handleDelete = (id) => {
    const newBlogs = blogs.filter(blog => blog.id !== id);
    setBlogs(newBlogs);
  }
  const [name,Setname]='luigi ';

useEffect(()=>{
  console.log('mario');

},[name]);


  return (
    <div className="home">
   <BlogList blogs={blogs} title='All Blogs are Here!'handleDelete={handleDelete}/>
   <BlogList blogs={blogs.filter((blog)=>blog.author==='mario' )} title="All Mario's blogs are here!" handleDelete={handleDelete}/>
   <button onClick={()=>Setname('luigi')}></button>
    </div>
  );
}
 
export default Home;
